INSERT INTO Annales VALUES(null,'QCM',1999,'',22,'Hématologie - Immunologie','Parmi les propositions suivantes concernant la leucémie lymphoïde chronique, laquelle (lesquelles) est (sont) exacte(s) ?','Elle s’accompagne souvent d’adénopathies','Au cours de son évolution, il y a souvent une augmentation des polynucléaires basophiles','Elle peut se compliquer de manifestations auto-immunes','Une transformation en leucémie aiguë est fréquente','Les lymphocytes sont souvent porteurs de CD5','ace');
INSERT INTO Annales VALUES(null,'QCM',1999,'',23,'Pharmacologie - Biochimie','Parmi les propositions suivantes, quelle(s) est (sont) celle(s) qui est (sont) exacte(s) ? Les sulfamides hypoglycémiants :','Peuvent être associés favorablement entre eux','Sont fortement liés aux protéines plasmatiques','Sont prescrits en 1ère intention chez le diabétique non insulino-dépendant sans surpoids','Sont contre-indiqués chez la femme enceinte','Ont, pour effet secondaire principal, l’hypoglycémie','bcde');
INSERT INTO Annales VALUES(null,'QCM',1999,'',24,'Pharmacologie - Biochimie','Parmi les propositions suivantes, laquelle (lesquelles) est (sont) exacte(s) ? Les inhibiteurs de l’enzyme de conversion exercent des effets bénéfiques dans l’insuffisance cardiaque car :','Ils ont un effet inotrope positif direct','Ils exercent des effets vasodilatateurs','Ils stimulent les récepteurs ß-adrénergiques cardiaques','Ils diminuent les taux circulants d’aldostérone','Ils augmentent la pénétration calcique dans le myocyte cardiaque','bd');
