INSERT INTO Annales VALUES(null,'QCM',1999,'',27,'Chimie - Toxicologie','Parmi les propriétés suivantes du monoxyde de carbone, indiquer celle(s) qui est (sont) exacte(s) :','c’est un gaz inodore','il est très soluble dans l’eau','il est adsorbable sur charbon actif','il est capable d’absorber les rayonnements Infra-Rouge','C’est un gaz réducteur','ade');
