INSERT INTO Annales VALUES(null,'QCM',1999,'',49,'Virologie - Infectiologie - Santé Publique','Parmi les propositions suivantes, laquelle (lesquelles) est (sont) exacte(s) ? Le virus de l’Hépatite A :','Est un virus à ADN','Appartient à la famille des Picornaviridae','Peut provoquer une cirrhose','Est transmis par voie féco-orale','Provoque une infection qui peut être prévenue par une vaccination','bde');
INSERT INTO Annales VALUES(null,'QCM',1999,'',50,'Pharmacologie - Hématologie - Hémostase','Parmi les propositions suivantes concernant l’aspirine, laquelle (lesquelles) est (sont) exacte(s) ?','La prolongation du temps de saignement par la prise d’aspirine ne s’exerce que pendant 6 heures','L’action anti-agrégante sur les plaquettes est liée à une inhibition de la glycoprotéine II b-III a','L’aspirine a prouvé son efficacité dans la prévention des rechutes d’infarctus du myocarde','L’aspirine potentialise l’action anticoagulante des antivitamines K','La dose habituelle anti-agrégante plaquettaire de l’aspirine est inférieure à 500 mg/24 heures','cde');
INSERT INTO Annales VALUES(null,'QCM',1999,'',51,'Biophysique - Biochimie - Chimie Analytique','Cocher les réponses exactes. En chromatographie liquide :','L’utilisation de silice greffée en phase inverse nécessite une élution par des solvants polaires','La détection par spectrométrie de masse est réalisable','Les molécules thermolabiles ne peuvent pas être séparées','Le gel de silice, base de fabrication des colonnes, possède un caractère polaire','Les molécules possédant une masse molaire supérieure à 500 Da sont inséparables','abd');
