INSERT INTO Annales VALUES(null,'QCS',2000,'',16,'Bactériologie - Pharmacologie - Infectiologie - Antibiotiques','Parmi les propriétés bactériologiques et pharmacocinétiques suivantes, laquelle n''est pas une caractéristique des aminosides ?','Forte liaison aux protéines plasmatiques','Effet post-antibiotique','Effet bactéricide concentration dépendant','Saturation de leur distribution tissulaire au niveau rénal','Existence de phénomènes de résistance par inactivation enzymatique','a');
