INSERT INTO Annales VALUES(null,'QCS',2001,'',10,'Bactériologie - Infectiologie','Parmi les bactéries suivantes, laquelle est un agent habituel d''otites chez l''enfant?','Neisseria gonorrhoeae','Vibrio cholerae','Haemophilus influenzae','Mycobacterium tuberculosis','Listeria monocytogenes','c');
INSERT INTO Annales VALUES(null,'QCS',2001,'',11,'Parasitologie - Infectiologie','Parmi les propositions suivantes concernant l''anguillulose, une seule est fausse. Laquelle?','C''est une parasitose opportuniste chez le sidéen','Elle se contracte par passage transcutané des larves infestantes','Elle peut persister plus de 10 ans.','Elle est accompagnée d''une hyperéosinophilie sanguine fluctuante','Elle se traite par l''ivermectine (STROMECTOL®).','a');
