INSERT INTO Annales VALUES(null,'QCM',2002,'S',40,'Biochimie - Endocrinologie','Parmi les propositions suivantes, indiquer la (les) réponse(s) exacte(s). Une cétonémie caractérise :','L’état de jeûne prolongé','Le diabète insipide','Le coma diabétique dû à un diabète insulino-dépendant','L’insuffisance rénale','Le coma','ac');
INSERT INTO Annales VALUES(null,'QCM',2002,'S',41,'Pharmacologie - Bactériologie - Infectiologie - Antibiotiques','Parmi les propositions suivantes concernant la sulfadiazine (ADIAZINE®) indiquer celle(s) qui est (sont) exacte(s) :','Elle fait partie des sulfamides antibactériens','Elle est très mal résorbée par voie digestive','Elle est métabolisée par glucuronoconjugaison','Elle est contre-indiquée en cas de déficit en G6PD','Elle est contre-indiquée en cas de traitement par les anticoagulants oraux','ad');
