INSERT INTO Annales VALUES(null,'QCM',2002,'S',50,'Chimie - Toxicologie','Parmi les affirmations suivantes concernant les homologues du benzène, laquelle (lesquelles) est (sont) exacte(s) ?','Le toluène est le méthylbenzène','Ce sont des irritants des muqueuses','Leur élimination se fait essentiellement par voie pulmonaire','Ils présentent une toxicité cardiaque','Ils subissent un métabolisme oxydatif','abe');
INSERT INTO Annales VALUES(null,'QCM',2002,'S',51,'Immunologie - Biochimie','Indiquer la ou les réponses exactes. Les anticorps :','Sont des gammaglobulines','Sont produits par les lymphocytes T','Permettent de réaliser des sérothérapies préventives','Sont produits par les macrophages','Sont produits par les plasmocytes','ace');
