INSERT INTO Annales VALUES(null,'QCM',2002,'S',56,'Bactériologie - Virologie - Infectiologie','Dans la liste suivante, quelle(s) est ( sont) la (les) proposition(s) exacte(s) concernant Vibrio cholerae ?','C’est une bactérie à Gram négatif de forme incurvée','C’est une bactérie mobile','Le pouvoir pathogène nécessite l’invasion intracellulaire des entérocytes','La toxine cholérique stimule l’adénylcyclasse des entérocytes','Les selles émises au cours du choléra sont muco-purulo-sanglantes','abd');
INSERT INTO Annales VALUES(null,'QCM',2002,'S',57,'Bactériologie - Infectiologie','Parmi les propriétés suivantes, quelles sont celles qui caractérisent Mycobacterium tuberculosis?','Résistance à la décoloration par les acides forts','Anaérobiose stricte','Croissance en 48 heures','Cultive sur milieu à base d’oeuf','Est sensible à la pénicilline G','ad');
