INSERT INTO Annales VALUES(null,'QCM',2003,'N',56,'Immunologie - Biochimie - Biophysique','Parmi les propositions suivantes, laquelle (lesquelles) est (sont) exacte(s) ? L’immunoanalyse :','Permet le dosage des antigènes','Ne permet pas le dosage des anticorps','Nécessite obligatoirement un anticorps marqué','Peut utiliser la chimiluminescence comme méthode de détection','Est réservée aux laboratoires possédant une autorisation spéciale','ad');
