INSERT INTO Annales VALUES(null,'QCM',2005,'N',30,'Bactériologie - Infectiologie - Antibiotiques','Quelle(s) est (sont) la (les) affirmation(s) exacte(s) concernant les fluoroquinolones?','Leur structure de base est l''acide quinoléine-3-carboxylique','L''un de leurs effets indésirables est la photosensibilisation','Elles inhibent l''activité enzymatique des topoisomérases de type II','Elles sont actives sur les spirochètes','Elles sont fortement fixées aux protéines plasmatiques','abc');
