INSERT INTO Annales VALUES(null,'QCS',2007,'N',24,'Pharmacologie - Chimie','Parmi les propositions suivantes, une seule est fausse, laquelle ? La chlorpromazine (LARGACTIL®) :','Possède une structure phénothiazinique','Engendre des hypertensions artérielles','Provoque une sécheresse buccale','Bloque les récepteurs dopaminergiques centraux','Entraîne des dyskinésies bucco-faciales','b');
